                  <span
                    style={{
                      color: "#FAC300",
                    }}
                  >
                    {" "}
                    12KW/H{" "}
                  </span>
                </Card.Text>
                <Card.Text
                  style={{
                    padding: "0",
                    marginTop: "-15px",
                  }}
                >
                  Location: Living Room
                </Card.Text>
                <Card.Text
                  style={{
                    padding: "0",
                    marginTop: "-15px",
                  }}
                >
                  ID: 6539392013
                </Card.Text>
                {studyOne ? (
                  <Button
                    variant="primary"
                    onClick={() => {
                      setStudyOne(!studyOne);
                    }}
                  >
                    Turn OFF
                  </Button>
                ) : (
                  <Button
                    variant="primary"
                    onClick={() => {
                      setStudyOne(!studyOne);
                    }}
                  >
                    Turn ON
                  </Button>
                )}
              </Card.Body>
            </Card>
            <Card
              style={{
                width: "18rem",
                marginLeft: "5vw",
                marginTop: "5vh",
                marginRight: "2vw",
                marginBottom: "5vw",
                float: "left",
              }}
            >
              <Card.Img variant="top" src="lightbulb.png" />
              <Card.Body>
                <Card.Title>Phillips Hue Lightbulb</Card.Title>
                <Card.Text>
                  Status:{" "}
                  {studyTwo ? (
                    <span
                      style={{
                        color: "green",
                      }}
                    >
                      {" "}
                      ON{" "}
                    </span>
